mapper.update:
UPDATE STARCLOUDPORTAL.SCE_ENTITY_AGENT
SET IS_ACTIVATE=#{isActivate}
WHERE ID=#{id}

mapper.select:
SELECT a.ID,a.AGENT_NAME,a.IS_ACTIVATE,GRADE,u.LOGIN_PERMISSION_STATUS,a.AGENT_AREA FROM STARCLOUDPORTAL.SCE_COMMON_USER u,STARCLOUDPORTAL.SCE_ENTITY_AGENT a WHERE 1=1
AND AGENT_NAME LIKE CONCAT(CONCAT('%',#{agentName}),'%')
AND GRADE=#{grade}
AND AGENT_AREA=#{agentArea}
AND u.USER_TYPE=6 AND a.ID=u.ORGANIZATION_ID AND u.IS_DELETE=1 AND a.IS_DELETE=1

mapper.update:
UPDATE STARCLOUDPORTAL.SCE_COMMON_USER
SET IS_DELETE=0
WHERE ORGANIZATION_ID=#{id}
