mapper.update:
update STARCLOUDPORTAL.SCE_ORDER_REFUND

    REFUND_STATE = #{REFUND_STATE} ,
    EXPENDITURE_TIME = #{EXPENDITURE_TIME} ,
    REASON = #{REASON}

where ID = #{ID}

mapper.select:
SELECT sor.ID, soi.ACTUAL_PAYMENT, soi.ORDER_ID, soi.PRODUCT_TYPE_CODE, soi.PRODUCT_NAME, sor.APPLY_TIME, sor.REFUND_STATE, sor.EXPENDITURE_TIME FROM STARCLOUDPORTAL.SCE_ORDER_REFUND sor LEFT JOIN STARCLOUDPORTAL.SCE_ORDER_INFO soi ON sor.ORDER_ID = soi.ORDER_ID WHERE sor.IS_DELETE = 1
and sor.ID = #{ID}
and sor.ORDER_ID = #{ORDER_ID}
and sor.APPLY_TIME >= TO_DATE(#{start_time}, 'YYYY-MM-DD HH24-MI-SS')
and sor.APPLY_TIME <= TO_DATE(#{end_time}, 'YYYY-MM-DD HH24-MI-SS')
