mapper.update:
update STARCLOUDPORTAL.SCE_COUPON set REVIEW_STATE = #{REVIEW_STATE}
, REVIEW_USER_ID = #{REVIEW_USER_ID} , REVIEW_DATE=#{REVIEW_DATE}

    , REVIEW_COMMENT=#{REVIEW_COMMENT}

where COUPON_CODE = #{COUPON_CODE}

mapper.select:
SELECT A.COUPON_CODE,COUPONTYPEDIC.COUPON_TYPE_CODE,COUPONTYPEDIC.COUPON_TYPE_NAME, REBATEDETAIL.REBATE_DETAIL,TEMP.GOODS_TYPE_NAME,A.VALID_DATE_FALG, CASE WHEN A.VALID_DATE_FALG = 0 THEN '永久有效' ELSE TO_CHAR(A.START_DATE,'yyyy-MM-dd HH24:mi:ss')||'-'||TO_CHAR(A.END_DATE,'yyyy-MM-dd HH24:mi:ss') END AS VALID_DATE, A.USE_TIMES_FLAG, CASE WHEN A.USE_TIMES_FLAG= 0 THEN '不限次数' ELSE TO_CHAR(A.TOTAL_USE_TIMES)||'次' END AS USE_TIMES, A.USED_TIMES, A.OVER_FLAG, CASE WHEN A.OVER_FLAG = 1 THEN '可叠加' ELSE '不可叠加' END AS OVER_FLAG_DEF, A.REVIEW_STATE, CASE WHEN A.REVIEW_STATE = 'T' THEN '待审核' WHEN A.REVIEW_STATE = 'F' THEN '被驳回' ELSE '审核通过' END AS REVIEW_STATE_DEF FROM STARCLOUDPORTAL.SCE_COUPON A
LEFT JOIN STARCLOUDPORTAL.SCE_COUPON_REBATE_DETAIL REBATEDETAIL ON A.COUPON_CODE = REBATEDETAIL.COUPON_CODE LEFT JOIN STARCLOUDPORTAL.SCE_COUPON_TYPE_DIC COUPONTYPEDIC ON REBATEDETAIL.COUPON_TYPE_CODE = COUPONTYPEDIC.COUPON_TYPE_CODE
LEFT JOIN (SELECT GOODSTYPEREL.COUPON_CODE AS COUPON_CODE,WMSYS.WM_CONCAT(GOODSTYPE.GOODS_TYPE_NAME) AS GOODS_TYPE_NAME FROM STARCLOUDPORTAL.SCE_COUPON_GOODSTYPE_REL GOODSTYPEREL LEFT JOIN STARCLOUDPORTAL.SCE_GOODS_TYPE_DIC GOODSTYPE ON GOODSTYPE.GOODS_TYPE_CODE = GOODSTYPEREL.GOODS_TYPE_CODE WHERE GOODSTYPE.IS_DELETE=1 AND GOODSTYPE.STATE='E' GROUP BY GOODSTYPEREL.COUPON_CODE ) TEMP ON A.COUPON_CODE = TEMP.COUPON_CODE WHERE A.STATE='E' AND A.IS_DELETE=1 and A.USER_ID = #{USER_ID}

SELECT A.COUPON_CODE,A.USER_ID,B.USER_NAME,COUPONTYPEDIC.COUPON_TYPE_CODE, COUPONTYPEDIC.COUPON_TYPE_NAME,TEMP.GOODS_TYPE_NAME,A.USE_TIMES_FLAG, CASE WHEN A.USE_TIMES_FLAG= 0 THEN '不限' ELSE "TO_CHAR"(A.TOTAL_USE_TIMES)||'次' END AS USE_TIMES, A.USED_TIMES,A.OVER_FLAG, CASE WHEN A.OVER_FLAG = 1 THEN '是' ELSE '否' END AS OVER_FLAG_DEF,A.REVIEW_STATE, CASE WHEN A.REVIEW_STATE = 'T' THEN '待审核' WHEN A.REVIEW_STATE = 'F' THEN '被驳回' ELSE '审核通过' END AS REVIEW_STATE_DEF, TO_CHAR(A.SUBMIT_DATE,'yyyy-MM-dd HH24:mi:ss') AS SUBMIT_DATE,NVL(A.REVIEW_COMMENT, ' ') AS REVIEW_COMMENT, TO_CHAR(A.REVIEW_DATE,'yyyy-MM-dd HH24:mi:ss') AS REVIEW_DATE FROM STARCLOUDPORTAL.SCE_COUPON A LEFT JOIN STARCLOUDPORTAL.SCE_COMMON_USER B ON A.USER_ID = B.USER_ID
LEFT JOIN STARCLOUDPORTAL.SCE_COUPON_REBATE_DETAIL REBATEDETAIL ON A.COUPON_CODE = REBATEDETAIL.COUPON_CODE LEFT JOIN STARCLOUDPORTAL.SCE_COUPON_TYPE_DIC COUPONTYPEDIC ON REBATEDETAIL.COUPON_TYPE_CODE = COUPONTYPEDIC.COUPON_TYPE_CODE
LEFT JOIN (SELECT GOODSTYPEREL.COUPON_CODE AS COUPON_CODE,WMSYS.WM_CONCAT(GOODSTYPE.GOODS_TYPE_NAME) AS GOODS_TYPE_NAME FROM STARCLOUDPORTAL.SCE_COUPON_GOODSTYPE_REL GOODSTYPEREL LEFT JOIN STARCLOUDPORTAL.SCE_GOODS_TYPE_DIC GOODSTYPE ON GOODSTYPE.GOODS_TYPE_CODE = GOODSTYPEREL.GOODS_TYPE_CODE WHERE GOODSTYPE.IS_DELETE=1 AND GOODSTYPE.STATE='E' GROUP BY GOODSTYPEREL.COUPON_CODE ) TEMP ON A.COUPON_CODE = TEMP.COUPON_CODE WHERE A.STATE='E' AND A.IS_DELETE=1
and A.COUPON_CODE LIKE CONCAT(CONCAT('%',#{COUPON_CODE}),'%')
and B.USER_NAME LIKE CONCAT(CONCAT('%',#{USER_NAME}),'%')
and COUPONTYPEDIC.COUPON_TYPE_CODE=#{COUPON_TYPE_CODE}
and A.REVIEW_STATE=#{REVIEW_STATE}
and A.OVER_FLAG=#{OVER_FLAG}
