mapper.select:
SELECT ORDER_NO, D.ID, D.APPLY_TIME, D.APPLY_USER_ID, D.INVOICE_AMOUNT, D.INVOICE_STATUS , D.IS_DELETE, D.RECIPIENTS, D.POST_ADDRESS, D.TELEPHONE_NUMBER, D.PROVINCE , D.CITY, D.AREA, D.INVOICE_TITLE, D.OPEN_TYPE, D.INVOICE_CONTENT , D.TAX_REGISTRATION_NO, D.DEPOSIT_BANK, D.BANK_ACCOUNT, D.REGISTRATION_LOCATION, D.REGISTRATION_TELEPHONE , D.INVOICE_CODE, D.INVOICE_NO, D.EXPRESS_COMPANY, D.COURIER_NUMBER FROM STARCLOUDPORTAL.SCE_ORDER_INVOICE_INFO D INNER JOIN ( SELECT WMSYS.WM_CONCAT(C.ORDER_ID) AS ORDER_NO, B.ID FROM STARCLOUDPORTAL.SCE_ORDER_INVOICE_REL A LEFT JOIN STARCLOUDPORTAL.SCE_ORDER_INVOICE_INFO B ON A.ORDER_INVOICE_ID = B.ID LEFT JOIN STARCLOUDPORTAL.SCE_ORDER_INFO C ON A.ORDER_ID = C.ORDER_ID GROUP BY B.ID ) E ON D.ID = E.ID
AND D.INVOICE_STATUS = #{INVOICE_STATUS}
AND ORDER_NO LIKE CONCAT(CONCAT('%',#{ORDER_NO}),'%')
AND D.OPEN_TYPE = #{OPEN_TYPE}
AND D.INVOICE_CONTENT = #{INVOICE_CONTENT}
ORDER BY D.APPLY_TIME DESC

mapper.insert:
insert into STARCLOUDPORTAL.SCE_INVOICE_ADDRESS
ID, ORGANIZATION_ID, RECIPIENTS, POST_ADDRESS, TELEPHONE_NUMBER, IS_DELETE, PROVINCE, CITY, AREA
#{ID}, #{ORGANIZATION_ID}, #{RECIPIENTS}, #{POST_ADDRESS}, #{TELEPHONE_NUMBER}, #{IS_DELETE}, #{PROVINCE}, #{CITY}, #{AREA}
