mapper.update:
UPDATE STARCLOUDPORTAL.SCE_COMMON_USER
SET IS_DELETE=0
WHERE USER_ID=#{id}

UPDATE STARCLOUDPORTAL.SCE_COMMON_USER_PASSWORD
SET PASSWORD=#{pwd}
WHERE USER_ID=#{id}

UPDATE
STARCLOUDPORTAL.SCE_COMMON_USER
SET LOGIN_PERMISSION_STATUS = #{newStatus}
WHERE
USER_ID = #{id}

UPDATE STARCLOUDPORTAL.SCE_ENTITY_SCHOOL
SET IS_DELETE=0
WHERE ID=#{id}
AND INSTITUTION_ID=#{institutionId}

mapper.select:
SELECT u.USER_ID,i.INSTITUTION_NAME,u.LOGIN_NAME,i.ID,u.LOGIN_PERMISSION_STATUS,u.IS_FIRST_LOGIN FROM STARCLOUDPORTAL.SCE_COMMON_USER u INNER JOIN STARCLOUDPORTAL.SCE_ENTITY_INSTITUTION i ON u.ORGANIZATION_ID=i.ID WHERE u.IS_DELETE=1 AND i.IS_DELETE=1
AND i.INSTITUTION_NAME LIKE CONCAT(CONCAT('%',#{institutionName}),'%')
AND i.ID=#{id}
AND u.LOGIN_PERMISSION_STATUS=#{loginPermissionStatus}
AND u.USER_TYPE=7

mapper.update:
UPDATE STARCLOUDPORTAL.SCE_COMMON_USER_PASSWORD
SET PASSWORD=#{password}
WHERE USER_ID=(SELECT USER_ID FROM STARCLOUDPORTAL.SCE_COMMON_USER WHERE LOGIN_NAME=#{loginName})

mapper.select:
SELECT u.USER_ID,USER_NAME,LOGIN_NAME,GENDER,PHONE_NUMBER,ACCOUNT_STATUS,POSITION
FROM STARCLOUDPORTAL.SCE_COMMON_USER u
LEFT JOIN STARCLOUDPORTAL.SCE_INFO_TEACHER t ON u.USER_ID=t.USER_ID AND t.IS_DELETE=1
WHERE u.USER_TYPE=2
AND u.IS_DELETE=1
AND u.ORGANIZATION_ID=#{organizationId}
AND u.USER_ID NOT IN (SELECT USER_ID FROM STARCLOUDPORTAL.SCE_TRANSFER_APPLY)

    AND USER_NAME LIKE CONCAT(CONCAT('%',#{userName}),'%')


    AND LOGIN_NAME LIKE CONCAT(CONCAT('%',#{loginName}),'%')


    AND GENDER=#{gender}


    AND POSITION=#{position}


    AND ACCOUNT_STATUS=#{accountStatus}

mapper.update:
UPDATE STARCLOUDPORTAL.SCE_INFO_TEACHER
SET IS_DELETE=0
WHERE USER_ID=#{id}
