mapper.update:
UPDATE STARCLOUDPORTAL.SCE_COMMON_USER
SET IS_DELETE=0
WHERE USER_ID=#{id}

UPDATE STARCLOUDPORTAL.SCE_COMMON_USER_PASSWORD
SET PASSWORD=#{pwd}
WHERE USER_ID=#{id}

UPDATE
STARCLOUDPORTAL.SCE_COMMON_USER
SET LOGIN_PERMISSION_STATUS = #{newStatus}
WHERE
USER_ID = #{id}

UPDATE STARCLOUDPORTAL.SCE_ENTITY_SCHOOL
SET IS_DELETE=0
WHERE ID=#{id}
AND INSTITUTION_ID=#{institutionId}

mapper.select:
SELECT u.USER_ID,i.INSTITUTION_NAME,u.LOGIN_NAME,i.ID,u.LOGIN_PERMISSION_STATUS FROM STARCLOUDPORTAL.SCE_COMMON_USER u INNER JOIN STARCLOUDPORTAL.SCE_ENTITY_INSTITUTION i ON u.ORGANIZATION_ID=i.ID WHERE u.IS_DELETE=1 AND i.IS_DELETE=1
AND i.INSTITUTION_NAME LIKE CONCAT(CONCAT('%',#{institutionName}),'%')
AND i.ID=#{id}
AND u.LOGIN_PERMISSION_STATUS=#{loginPermissionStatus}
AND u.USER_TYPE=7
